#include<stdio.h>
#define MAX 100
#include<stdbool.h>
#include<string.h>
#include<math.h>
typedef struct 
{
    char arr[MAX];
    int top;
}Stack;
void init(Stack *s)
{
    s->top=-1;
}
bool isFull(Stack s)
{
    if(s.top==MAX-1)
    {
        return true;
    }
    return false;
}
bool isEmpty(Stack s)
{
    if(s.top==-1){
        return true;
    }
    else
    {
        return false;
    }
}
void push(Stack *s,int x)
{
    if(isFull(*s))
    {
        printf("stack day");
        return;
    }
    s->arr[++s->top]=x;
}
int size(Stack s)
{
    return s.top+1;
}
bool isValid(char *s)
{
    bool comparsion=false;
    int length=strlen(s);
    if(length%2!=0)
    {
        return false;
    }
    Stack chuoi;
    init(&chuoi);
   for(int i=0;i<length;i++)
   {
    if(s[i]=='{'||s[i]=='('||s[i]=='[')
    {
        push(&chuoi,s[i]);
    }
    else
    {
        comparsion=true;
        if(isEmpty(chuoi))
        {
            return false;
        }
        else
        {
            if((s[i]==')'&&chuoi.arr[chuoi.top]=='(')||(s[i]=='}'&&chuoi.arr[chuoi.top]=='{')||(s[i]==']'&&chuoi.arr[chuoi.top]=='['))
            {
             chuoi.top--;
            }
            else
            {
                return false;
            }
        }
    }
   } 
   if(comparsion==false)
   {
    return false;
   }
   return true;
}
